mapper.select:
SELECT t.id id, t.attribute_key attributeKey, t.attribute_value attributeValue, t.attribute_module attributeModule, t.attribute_enable attributeEnable, t.environment environment, t.created_date createDate, t.last_update_date lastUpdateDate FROM t_system_config t WHERE 1 = 1
and t.attribute_key = #{attributeKey}
and t.attribute_enable = #{attributeEnable}
and (t.attribute_module = 'all'
or t.attribute_module = #{attributeModule}
) and (t.environment = 'all'
or t.environment = #{environment}
) order by t.last_update_date desc
